  }
}



checkCell = (r,c,numbers,squares) => {

  var rowStart = this.getStartIndex(r);
  var colStart = this.getStartIndex(c);
console.log(rowStart,colStart);
  for(let row=rowStart; row<=rowStart+2; row++){
