        }
        console.log('row',numbers);
      }
    }
  }
    return numbers;
}



checkCol = (r,c,numbers,squares) => {

  for(let i=0; i<9; i++){

    if(i != r){
      if(squares[i][c].value != "X"){
        if(numbers.indexOf(squares[i][c].value)!=-1){
         numbers.splice(numbers.indexOf(squares[i][c].value),1);
        }
        //console.log('rr',numbers);
      }
    }
  }
    return numbers;
}

displayCell(i,j) {
  return (
    <Square
      value={this.state.squares[i][j]?this.state.squares[i][j].value:""}
      //onClick={() => this.onClickHandler(i)}
    />
  );
}

displayPlayAgainCell() {
  return (
    <button
    style={{backgroundColor:"blue", color:"white", height:"40px", width:"100px"}}
      className="square"
      onClick={() => this.onPlayAgainHandler()}
    >
      Play Again
