import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


function Square(props) {
    return (
      <button
        className="square"
        onClick={props.onClick}
      >
        {props.value}
      </button>
    );
  }

  function Square2(props) {
      return (

          <input size="2" maxlength="1" readOnly={props.readOnlyVal}
            type="text"
            placeholder=""
            name={props.value}
            value={props.value}
            //onChange={props.onChange}
            //onChange={props.onChange}
            onChange={(event) => props.onChangeHandler}
          />

      );
    }

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        squares: Array(6).fill(''),
        solution: Array(6).fill(''),
        puzzle: Array(6).fill(''),
        //board: Array(6).fill(null),
        board: Array(2).fill({value:'',given:false}),
        xIsNext: true,
        readOnlyVal:false
        };
        this.valChangeHandler = this.valChangeHandler.bind(this);

    }

  handleClick(i) {
    const squares = this.state.squares.slice();
    if (calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? 'X' : 'O';
    this.setState({
      squares: squares,
      xIsNext: !this.state.xIsNext,
    });
  }

  displayCell(i) {
    return (
      <Square
        value={this.state.squares[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }

  componentDidMount(){
    // var solution = this.state.solution;
    // var puzzle = this.state.puzzle;
    // var board = puzzle;
    var solution = [[{value:1,pencilMark:[],visited:[]},{value:2,pencilMark:[],visited:[]},
    {value:9,pencilMark:[],visited:[]}],[{value:4,pencilMark:[],visited:[]},{value:5,pencilMark:[],visited:[]},
    {value:6,pencilMark:[],visited:[]}]];
    var puzzle = [[{value:1,given:true},{value:undefined,given:false},{value:undefined,given:false}],
    [{value:undefined,given:false},{value:5,given:true},{value:undefined,given:false}]];

    //const board = puzzle.slice();
    const board = this.deepCopy(puzzle);

if(board ===  puzzle){console.log('equal')}else{console.log('not equal')}



  //  this.setState({solution: solution, puzzle: puzzle, board: board});
    this.setState({solution: solution, puzzle: puzzle, board: board}, function () {
      console.log('in mount bo',this.state.board);
      console.log('in mount pu',this.state.puzzle);
      console.log('in mount sol',this.state.solution);
    });
  }

  onPlayAgainHandler(){
    // var board = [[{value:1,given:true},{value:null,given:false},{value:undefined,given:false}],
    // [{value:undefined,given:false},{value:5,given:true},{value:undefined,given:false}]];
    var board = this.deepCopy(this.state.puzzle.slice());
    console.log('in onPlayAgainHandler before b',board);

    this.setState({board: board}, function () {
      console.log('in onPlayAgainHandler fter b',this.state.board);
    });
  }

  deepCopy(obj) {
      var rv;

      switch (typeof obj) {
          case "object":
              if (obj === null) {
                  // null => null
                  rv = null;
              } else {
                  switch (toString.call(obj)) {
                      case "[object Array]":
                          // It's an array, create a new array with
                          // deep copies of the entries
                          console.log("i hee",obj)
                          rv = obj.map(o => this.deepCopy(o));
                            console.log("i hee",rv)
                          break;
                      default:
                      console.log("i heer 2",obj)
                          rv = Object.keys(obj).reduce((prev, key) => {
                            console.log("i heer 2 pk",prev,key,obj[key])
                              prev[key] = this.deepCopy(obj[key]);
                              return prev;
                          }, {});
                          break;
                  }
              }
              break;
          default:
              // It's a primitive, copy via assignment
              rv = obj;
              break;
      }
      return rv;
  }

  onStopHandler(i) {
    //const squares = this.state.squares.slice();
    var nrows = 9;
    var ncols = 9;
    var squares =new Array(nrows);

    for (var i=0;i<nrows;i++) {
      squares[i]=new Array(ncols)
    }

    for (var i=0;i<nrows;i++) {
       for (var j=0;j<nrows;j++) {
         squares[i][j] = {value:"X",pencilMark:[],visited:[]};
       }
    }

    this.setState({squares: squares});
  }

  valChangeHandler = (i,j,event) => {
    //event.preventDefault();
    const board = this.state.board.slice();

if(event.target.value){
    board[i][j].value = Number(event.target.value);

console.log(board[i][j].value,i,j);
    //this.setState({board: board});
    this.setState({board: board}, function () {
      console.log('in val b',this.state.board);
      console.log('in val pu',this.state.puzzle);
    });
    }
  }

  displayPlayAgainCell() {
    return (
      <button
      style={{backgroundColor:"blue", color:"white", height:"40px", width:"100px"}}
        className="square"
        onClick={() => this.onPlayAgainHandler()}
      >
        Play Again
      </button>
    );
  }



  displaySeeSolutionsCell() {
    return (
      <button
      style={{backgroundColor:"blue", color:"white", height:"40px", width:"100px"}}
        className="square"
        onClick={() => this.onSeeSolutionsHandler()}
      >
        Show Solution
      </button>
    );
  }

  displayProgressCell() {
    return (
      <button
      style={{backgroundColor:"blue", color:"white", height:"40px", width:"100px"}}
        className="square"
        onClick={() => this.onStopHandler()}
      >
        How am I doing?
      </button>
    );
  }

  displayCellForm(i,j) {
    return (
      // <Square2
      //   value={this.state.board[i][j]?this.state.board[i][j].value:""}
      //
      //   //readOnlyVal={this.state.readOnlyVal}
      //   // readOnlyVal={this.state.board[i][j].given}
      //
      //   onChangeHandler={(event) => this.valChangeHandler(i,j)}
      //   //onChange={this.valChangeHandler(i,j)}
      // />
      <input size="2" maxLength="1"
        type="text"
        placeholder=""
        readOnly={this.state.board[i][j]?this.state.board[i][j].given:false}
        key={this.state.board[i][j]?this.state.board[i][j].value+'['+i+']'+'['+j+']':undefined}
        defaultValue={this.state.board[i][j]?this.state.board[i][j].value:""}
        onChange={(event) => this.valChangeHandler(i,j,event)}
      />

    );
  }
  render() {
    const winner = calculateWinner(this.state.squares);
    let status;
    if (winner) {
      status = 'Winner: ' + winner;
    } else {
      status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
    }

    return (
      <div>
        <div className="status">{status}</div>

        <form  >
        <div className="board-row">
          {this.displayCellForm(0,0)}
          {this.displayCellForm(0,1)}
          {this.displayCellForm(0,2)}

        </div>
        <div className="board-row">
          {this.displayCellForm(1,0)}
          {this.displayCellForm(1,1)}
          {this.displayCellForm(1,2)}
        </div>
        </form>
        <br></br>
        <div className="board-row">
          {this.displayCellForm(0,0)}
          {this.displayCellForm(0,1)}
          {this.displayCellForm(0,2)}
        </div>
        <div className="board-row">
          {this.displayCellForm(1,0)}
          {this.displayCellForm(1,1)}
          {this.displayCellForm(1,2)}
        </div>
        <div className="board-row">
          {this.displayPlayAgainCell()}
        </div>
        <br></br>
        <div className="board-row">
          {this.displaySeeSolutionsCell()}
        </div>
        <br></br>
        <div className="board-row">
          {this.displayProgressCell()}
        </div>
      </div>
    );
  }
}

class TTT extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

