import React, { Component } from 'react';
import "react-router";
import {
  BrowserRouter,
  Route,
  Link
} from 'react-router-dom'
import Create from "./Create.js";

class List extends Component {
	constructor(props) {
      super(props); 	
      this.handleClickEdit = this.handleClickEdit.bind(this);
      this.handleClickShow = this.handleClickShow.bind(this);       
  }


handleClickEdit = (index,e) => {
  e.preventDefault()
  this.props.history.push("/products/"+index+"/edit/");
}

handleClickShow = (index,e) => {
  e.preventDefault()
  this.props.history.push("/products/"+index+"");
}

  render(){
    const listItems = this.props.products.map((itemString, index) => {
      return  <div key={index}>                 
                {itemString.id} {itemString.name}  {itemString.price} {itemString.qty}
                <button onClick={(e) => this.handleClickEdit(index, e)}type="button">Edit</button>              
                <button onClick={(e) => this.handleClickShow(itemString.id, e)}type="button">Details</button>            
              </div>   
      });
		return (
      <div>
        <h1>Products List</h1>
        <Link to="/products/new">Add New Product</Link>
        <Route
              exact path="/products/new"
              render={(props) => <Create {...props} addProduct={this.handleAddProduct} prd={this.state.prdInit} disp="New Product" buttonTxt="CREATE"/>}
            />
        {listItems}
      </div>
		)
	}
}
export default List;
