import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

function Square(props) {
  return (
    <button
      style={{backgroundColor:"grey", color:"white", height:"40px", width:"40px"}}
      className="square"
      onClick={props.onClick}
    >{props.value}
    </button>
  );
}

class Board extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
      cellValues: [],
      // cellValues: ["blue", "yellow", "white", "pink",
      // "yellow","red","white","red",
      // "purple","blue","purple","pink"],
      squares: Array(81).fill(""),
      //squares: [],
      guessOne: true,
      guessTwo: false,
      index1: false,
      };
  }

//   onClickHandler(i) {
//   const squares = this.state.squares.slice();
//
//   if(this.state.guessOne){
//     squares[i] = this.state.cellValues[i];
//
//     this.setState({
//       squares: squares,
//       guessOne: false,
//       guessTwo: true,
//       index1: i,
//     });
//   } else if(this.state.guessTwo){
//       squares[i] = this.state.cellValues[i];
//
//       this.setState({
//         squares: squares,
//         guessOne: true,
//         guessTwo: false
//       },() => {
//         if (!this.isMatch(this.state.index1,i)){
//           this.flipCells(this.state.index1,i);
//         }
//     });
//
//   }
// }


onPlayAgainHandler() {
  //const squares = Array(81).fill("grey");
  var squares = [];
  var nrows = 9;
  var ncols = 9;

  for (var i=0;i<nrows;i++) {
     squares[i] = Array(9).fill("");
  }

    const cellValues = this.state.cellValues.slice();
    const rowOneSeed = [...Array(9).keys()].map(x => ++x);
    console.log('ooooo',rowOneSeed);
    this.shuffleArray(rowOneSeed);
  //  console.log(rowOneSeed);
    //this.shuffleArray(cellValues);
    //squares.concat(rowOneSeed);
    for(let i=0; i<rowOneSeed.length; i++){
        squares[0][i] = rowOneSeed[i];
    }
    console.log(squares);
    //console.log(squares[0][0]);
    this.setState({squares: squares, cellValues: cellValues});



    //var numbers = "0123456789";

    for(let r=1; r<nrows; r++){
      for(let c=0; c<ncols; c++){
        var numbers = "123456789";
        console.log(numbers);
        console.log(r,c);
        numbers = this.checkCell(r,c,numbers,squares);
        numbers = this.checkRow(r,c,numbers,squares);
        //console.log(numbers);
        numbers = this.checkCol(r,c,numbers,squares);
        console.log('o',numbers);
        var randIndex = Math.floor(Math.random() * (numbers.length-1));
        console.log(randIndex);
        var val = numbers[randIndex];
        console.log(val);
        squares[r][c] = val;
      }
    }
    this.setState({squares: squares, cellValues: cellValues});
}

getStartIndex(index){
  switch(index) {
      case 0:
      case 1:
      case 2:
          return 0;
      case 3:
      case 4:
      case 5:
          return 3;
      case 6:
      case 7:
      case 8:
          return 6;
      default:
          return 0;
  }
}



checkCell = (r,c,numbers,squares) => {

  var rowStart = this.getStartIndex(r);
  var colStart = this.getStartIndex(c);
console.log(rowStart,colStart);
  for(let row=rowStart; row<=rowStart+2; row++){
    for(let col=colStart; col<=colStart+2; col++){
      if(!(row == r && col == c)){
        if(squares[row][col] != ""){
          numbers = numbers.replace(squares[row][col],'');
          ////console.log('rrrr',numbers);
        }
      }
    }
  }
  return numbers;
}

checkRow = (r,c,numbers,squares) => {
//console.log("HII");
  for(let i=0; i<9; i++){

    if(i != c){
      if(squares[r][i] != ""){
        //console.log("HII0");
        //console.log(squares[r][i]);
        numbers = numbers.replace(squares[r][i],'');
        console.log('r',numbers);
      }
    }
  }
    return numbers;
}



checkCol = (r,c,numbers,squares) => {

  for(let i=0; i<9; i++){

    if(i != r){
      if(squares[i][c] != ""){
        //console.log(squares[i][c]);
        numbers = numbers.replace(squares[i][c],'');
        //console.log('rr',numbers);
      }
    }
  }
    return numbers;
}

displayCell(i,j) {
  return (
    <Square
      value={this.state.squares[i][j]}
      //onClick={() => this.onClickHandler(i)}
    />
  );
}

displayPlayAgainCell() {
  return (
    <button
    style={{backgroundColor:"blue", color:"white", height:"40px", width:"100px"}}
      className="square"
      onClick={() => this.onPlayAgainHandler()}
    >
      Play Again
    </button>
  );
}

shuffleArray(array) {
  for (var i = array.length - 1; i > 0; i--) {
      var j = Math.floor(Math.random() * (i + 1));
      var temp = array[i];
      array[i] = array[j];
      array[j] = temp;
  }
}
render() {
  return (
    <div>
      <div className="board-row">
        {this.displayCell(0,0)}
        {this.displayCell(0,1)}
        {this.displayCell(0,2)}
        {this.displayCell(0,3)}
        {this.displayCell(0,4)}
        {this.displayCell(0,5)}
        {this.displayCell(0,6)}
        {this.displayCell(0,7)}
        {this.displayCell(0,8)}
      </div>
      <div className="board-row">
        {this.displayCell(1,0)}
        {this.displayCell(1,1)}
        {this.displayCell(1,2)}
        {this.displayCell(1,3)}
        {this.displayCell(1,4)}
        {this.displayCell(1,5)}
        {this.displayCell(1,6)}
        {this.displayCell(1,7)}
        {this.displayCell(1,8)}
      </div>
      <div className="board-row">
        {this.displayCell(2,0)}
        {this.displayCell(2,1)}
        {this.displayCell(2,2)}
        {this.displayCell(2,3)}
        {this.displayCell(2,4)}
        {this.displayCell(2,5)}
        {this.displayCell(2,6)}
        {this.displayCell(2,7)}
        {this.displayCell(2,8)}
      </div>
      <div className="board-row">
        {this.displayCell(3,0)}
        {this.displayCell(3,1)}
        {this.displayCell(3,2)}
        {this.displayCell(3,3)}
        {this.displayCell(3,4)}
        {this.displayCell(3,5)}
        {this.displayCell(3,6)}
        {this.displayCell(3,7)}
        {this.displayCell(3,8)}
      </div>
      <div className="board-row">
        {this.displayCell(4,0)}
        {this.displayCell(4,1)}
        {this.displayCell(4,2)}
        {this.displayCell(4,3)}
        {this.displayCell(4,4)}
        {this.displayCell(4,5)}
        {this.displayCell(4,6)}
        {this.displayCell(4,7)}
        {this.displayCell(4,8)}
      </div>
      <div className="board-row">
        {this.displayCell(5,0)}
        {this.displayCell(5,1)}
        {this.displayCell(5,2)}
        {this.displayCell(5,3)}
        {this.displayCell(5,4)}
        {this.displayCell(5,5)}
        {this.displayCell(5,6)}
        {this.displayCell(5,7)}
        {this.displayCell(5,8)}
      </div>
      <div className="board-row">
        {this.displayCell(6,0)}
        {this.displayCell(6,1)}
        {this.displayCell(6,2)}
        {this.displayCell(6,3)}
        {this.displayCell(6,4)}
        {this.displayCell(6,5)}
        {this.displayCell(6,6)}
        {this.displayCell(6,7)}
        {this.displayCell(6,8)}
      </div>
      <div className="board-row">
        {this.displayCell(7,0)}
        {this.displayCell(7,1)}
        {this.displayCell(7,2)}
        {this.displayCell(7,3)}
        {this.displayCell(7,4)}
        {this.displayCell(7,5)}
        {this.displayCell(7,6)}
        {this.displayCell(7,7)}
        {this.displayCell(7,8)}
      </div>
      <div className="board-row">
        {this.displayCell(8,0)}
        {this.displayCell(8,1)}
        {this.displayCell(8,2)}
        {this.displayCell(8,3)}
        {this.displayCell(8,4)}
        {this.displayCell(8,5)}
        {this.displayCell(8,6)}
        {this.displayCell(8,7)}
        {this.displayCell(8,8)}
      </div>
      <div className="board-row">
        {this.displayPlayAgainCell()}
      </div>
    </div>
  );
}
}

class Game extends React.Component {
render() {
  return (
      <div>
      <h1> Memory Game </h1>
        <Board />
      </div>
  );
}
}

export default Game;
