  validPencilMarks(row,col,board){
    var pM = [1,2,3,4,5,6,7,8,9];
    pM = this.checkCell(row,col,pM,board);
    pM = this.checkRow(row,col,pM,board);
    pM = this.checkCol(row,col,pM,board);
