import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

function Square(props) {
  return (
    <button
      style={{backgroundColor:props.value, color:"white", height:"40px", width:"40px"}}
      className="square"
      onClick={props.onClick}
    >
    </button>
  );
}

class Board extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
      cellValues: ["blue", "yellow", "white", "pink",
      "yellow","red","white","red",
      "purple","blue","purple","pink"],
      squares: Array(12).fill("grey"),
      guessOne: true,
      guessTwo: false,
      index1: false,
      };
  }

  onClickHandler(i) {
  const squares = this.state.squares.slice();

  if(this.state.guessOne){
    squares[i] = this.state.cellValues[i];

    this.setState({
      squares: squares,
      guessOne: false,
      guessTwo: true,
      index1: i,
    });
  } else if(this.state.guessTwo){
      squares[i] = this.state.cellValues[i];

      this.setState({
        squares: squares,
        guessOne: true,
        guessTwo: false
      },() => {
        if (!this.isMatch(this.state.index1,i)){
          this.flipCells(this.state.index1,i);
        }
    });

  }
}

flipCells = (index1, index2) => {
  const squares = this.state.squares.slice();
      squares[index1] = "grey";
      squares[index2] = "grey";
  let intervalId = setTimeout(() => {
    this.setState({
      squares: squares,
    });
  }, 1000);
};

isMatch(index1, index2){
  return this.state.squares[index1] == this.state.squares[index2];
}

displayCell(i) {
  return (
    <Square
      value={this.state.squares[i]}
      onClick={() => this.onClickHandler(i)}
    />
  );
}

displayPlayAgainCell() {
  return (
    <button
    style={{backgroundColor:"blue", color:"white", height:"40px", width:"100px"}}
      className="square"
      onClick={ () => {const squares = Array(12).fill("grey");
        const cellValues = this.state.cellValues.slice();
        this.shuffleArray(cellValues);
        this.setState({squares: squares, cellValues: cellValues})  }}
    >
      Play Again
    </button>
  );
}

shuffleArray(array) {
  for (var i = array.length - 1; i > 0; i--) {
      var j = Math.floor(Math.random() * (i + 1));
      var temp = array[i];
      array[i] = array[j];
      array[j] = temp;
  }
}
render() {
  return (
    <div>
      <div className="board-row">
        {this.displayCell(0)}
        {this.displayCell(1)}
        {this.displayCell(2)}
        {this.displayCell(3)}
        {this.displayCell(0)}
        {this.displayCell(1)}
        {this.displayCell(2)}
        {this.displayCell(3)}
      </div>
      <div className="board-row">
        {this.displayCell(4)}
        {this.displayCell(5)}
        {this.displayCell(6)}
        {this.displayCell(7)}
      </div>
      <div className="board-row">
        {this.displayCell(8)}
        {this.displayCell(9)}
        {this.displayCell(10)}
        {this.displayCell(11)}
      </div>
      <div className="board-row">
        {this.displayPlayAgainCell()}
      </div>
    </div>
  );
}
}

class Game extends React.Component {
render() {
  return (
      <div>
      <h1> Memory Game </h1>
        <Board />
      </div>
  );
}
}

export default Game;
