import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


function Square(props) {
    return (
      <button
        className="square"
        onClick={props.onClick}
      >
        {props.value}
      </button>
    );
  }

  function Square2(props) {
      return (

          <input size="2" maxlength="1" readOnly={props.readOnlyVal}
            type="text"
            placeholder=""
            name={props.value}
            value={props.value}
          />

      );
    }

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        squares: Array(9).fill(null),
        solution: Array(9).fill(null),
        puzzle: Array(9).fill(null),
        board: Array(9).fill(null),
        xIsNext: true,
        readOnlyVal:false
        };
    }

  handleClick(i) {
    const squares = this.state.squares.slice();
    if (calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? 'X' : 'O';
    this.setState({
      squares: squares,
      xIsNext: !this.state.xIsNext,
    });
  }

  displayCell(i) {
    return (
      <Square
        value={this.state.squares[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }

  componentDidMount(){
    // var solution = this.state.solution;
    // var puzzle = this.state.puzzle;
    // var board = puzzle;
    var solution =  [[{value:1,pencilMark:[],visited:[]},{value:2,pencilMark:[],visited:[]},
    {value:9,pencilMark:[],visited:[]}],[{value:4,pencilMark:[],visited:[]},{value:5,pencilMark:[],visited:[]},
    {value:6,pencilMark:[],visited:[]}]];
    var puzzle =  [[{value:1,given:true},{value:null,given:false},{value:null,given:false}],
    [{value:null,given:false},{value:5,given:true},{value:null,given:false}]];

    var board = puzzle;



    this.setState({solution: solution});
  }

  displayCellForm(i) {
    return (
      <Square2
        value={this.state.board[i]}
        readOnlyVal={this.state.readOnlyVal}
        // readOnlyVal={this.state.board[i].given}
        onClick={() => this.handleClick(i)}
      />
    );
  }
  render() {
    const winner = calculateWinner(this.state.squares);
    let status;
    if (winner) {
      status = 'Winner: ' + winner;
    } else {
      status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
    }

    return (
      <div>
        <div className="status">{status}</div>

        <form  >
        <div className="board-row">
          {this.displayCellForm(0)}
          {this.displayCellForm(1)}
          {this.displayCellForm(2)}
        </div>
        <div className="board-row">
          {this.displayCellForm(3)}
          {this.displayCellForm(4)}
          {this.displayCellForm(5)}
        </div>
        <div className="board-row">
          {this.displayCellForm(6)}
          {this.displayCellForm(7)}
          {this.displayCellForm(8)}
        </div>

        </form>
      </div>
    );
  }
}

class TTT extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

