import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function Square(props) {
  return (
    <button
      style={{backgroundColor:props.value, color:"white", height:"40px", width:"40px"}}
      className="square"
      onClick={props.onClick}
    >{props.value}
    </button>
  );
}

class Board extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
      cellValues: ["blue", "DarkKhaki", "yellow", "white", "Orange", "pink",
                   "LawnGreen", "BlanchedAlmond", "green", "yellow", "red", "white",
                   "red", "Fuchsia", "DarkOrchid", "brown", "Peru", "DarkOrchid",
                   "gold", "DodgerBlue", "Tomato", "DarkKhaki", "LightSteelBlue", "IndianRed",
                   "IndianRed", "blue", "purple", "pink", "Peru", "SlateBlue",
                   "Orange", "SlateBlue", "LightSteelBlue",  "green", "Turquoise", "Tan",
                   "purple", "violet", "violet", "brown", "DodgerBlue", "gold",
                   "Turquoise", "LawnGreen", "BlanchedAlmond", "Fuchsia", "Tan", "Tomato"
                 ],
      squares: Array(48).fill("grey"),
      guessOne: true,
      guessTwo: false,
      index1: false,
      playButtonText:"Play",
      buttonText:"See Solution"
      };
  }

  onClickHandler(i) {
    const squares = this.state.squares.slice();

    if(this.state.guessOne){
      squares[i] = this.state.cellValues[i];

      this.setState({
        squares: squares,
        guessOne: false,
        guessTwo: true,
        index1: i,
      });
    } else if(this.state.guessTwo){
        squares[i] = this.state.cellValues[i];

        this.setState({
          squares: squares,
          guessOne: true,
          guessTwo: false
        },() => {
          if (!this.isMatch(this.state.index1,i)){
            this.flipCells(this.state.index1,i);
          }
      });

    }
  }

flipCells = (index1, index2) => {
  const squares = this.state.squares.slice();
      squares[index1] = "grey";
      squares[index2] = "grey";
  let intervalId = setTimeout(() => {
    this.setState({
      squares: squares,
    });
  }, 1000);
};

isMatch(index1, index2){
  return this.state.squares[index1] == this.state.squares[index2];
}

displayCell(i) {
  return (
    <Square
      value={this.state.squares[i]}
      onClick={() => this.onClickHandler(i)}
    />
  );
}

displayPlayAgainCell() {
  return (
    <button
    style={{backgroundColor:"blue", color:"white", height:"40px", width:"100px"}}
      className="square"
      onClick={ () => {const squares = Array(48).fill("grey");var playButtonText = "Play Again";
        const cellValues = this.state.cellValues.slice();
        this.shuffleArray(cellValues);
        this.setState({squares: squares, cellValues: cellValues, playButtonText: playButtonText})  }}
    >
      {this.state.playButtonText}
    </button>
  );
}

shuffleArray(array) {
  for (var i = array.length - 1; i > 0; i--) {
      var j = Math.floor(Math.random() * (i + 1));
      var temp = array[i];
      array[i] = array[j];
      array[j] = temp;
  }
}
render() {
  return (
    <div>
      <div className="board-row">
        {this.displayCell(0)}
        {this.displayCell(1)}
        {this.displayCell(2)}
        {this.displayCell(3)}
        {this.displayCell(4)}
        {this.displayCell(5)}
        {this.displayCell(6)}
        {this.displayCell(7)}
      </div>
      <div className="board-row">
        {this.displayCell(8)}
        {this.displayCell(9)}
        {this.displayCell(10)}
        {this.displayCell(11)}
        {this.displayCell(12)}
        {this.displayCell(13)}
        {this.displayCell(14)}
        {this.displayCell(15)}
      </div>
      <div className="board-row">
        {this.displayCell(16)}
        {this.displayCell(17)}
        {this.displayCell(18)}
        {this.displayCell(19)}
        {this.displayCell(20)}
        {this.displayCell(21)}
        {this.displayCell(22)}
        {this.displayCell(23)}
      </div>
      <div className="board-row">
        {this.displayCell(24)}
        {this.displayCell(25)}
        {this.displayCell(26)}
        {this.displayCell(27)}
        {this.displayCell(28)}
        {this.displayCell(29)}
        {this.displayCell(30)}
        {this.displayCell(31)}
      </div>
      <div className="board-row">
        {this.displayCell(32)}
        {this.displayCell(33)}
        {this.displayCell(34)}
        {this.displayCell(35)}
        {this.displayCell(36)}
        {this.displayCell(37)}
        {this.displayCell(38)}
        {this.displayCell(39)}
      </div>
      <div className="board-row">
        {this.displayCell(40)}
        {this.displayCell(41)}
        {this.displayCell(42)}
        {this.displayCell(43)}
        {this.displayCell(44)}
        {this.displayCell(45)}
        {this.displayCell(46)}
        {this.displayCell(47)}
      </div>
      <br></br>
      <div className="board-row">
        {this.displayPlayAgainCell()}
      </div>
    </div>
  );
}
}

class MemoryGame extends React.Component {
render() {
  return (
      <div>
      <h1> Memory Game </h1>
        <Board />
      </div>
  );
}
}

export default MemoryGame;
