import "react-router";
import {
  BrowserRouter,
  Route,
  Link
} from 'react-router-dom'


class GameStats extends Component {
	constructor(props) {
      super(props);
      this.state = {
        statsList: [{gameId:'1',level:'easy',timeRBS:'5',timeNRBS:'6',timeLS:'1'},{gameId:'2',level:'easy',timeRBS:'6',timeNRBS:'7',timeLS:'8'},
      {gameId:'4',level:'medium',timeRBS:'51',timeNRBS:'6',timeLS:'1'},{gameId:'5',level:'hard',timeRBS:'61',timeNRBS:'7',timeLS:'8'},],
        stats:{gameId:'',level:'',timeRBS:'',timeNRBS:'',timeLS:''},
        idCounter:0
      }
      this.handleClickEdit = this.handleClickEdit.bind(this);
      this.handleClickShow = this.handleClickShow.bind(this);
  }


handleClickEdit = (index,e) => {
  e.preventDefault()
  this.props.history.push("/products/"+index+"/edit/");
}

handleClickShow = (index,e) => {
  e.preventDefault()
  this.props.history.push("/products/"+index+"");
}

