import React, { Component } from 'react';
import "react-router";
import {
  BrowserRouter,
  Route,
  Link
} from 'react-router-dom'


class GameStats extends Component {
	constructor(props) {
      super(props);
      this.state = {
        statsList: [{gameId:'1',level:'easy',timeRBS:'5',timeNRBS:'6',timeLS:'1'},{gameId:'2',level:'easy',timeRBS:'6',timeNRBS:'7',timeLS:'8'},
      {gameId:'4',level:'medium',timeRBS:'51',timeNRBS:'6',timeLS:'1'},{gameId:'5',level:'hard',timeRBS:'62',timeNRBS:'7',timeLS:'8'},],
        stats:{gameId:'',level:'',timeRBS:'',timeNRBS:'',timeLS:''},
        idCounter:0
      }
      this.handleClickEdit = this.handleClickEdit.bind(this);
      this.handleClickShow = this.handleClickShow.bind(this);
  }


handleClickEdit = (index,e) => {
  e.preventDefault()
  this.props.history.push("/products/"+index+"/edit/");
}

handleClickShow = (index,e) => {
  e.preventDefault()
  this.props.history.push("/products/"+index+"");
}

  render(){
    const listItems = this.state.statsList.map((stat, index) => {
      return  <div key={index}>
                <tr>{stat.gameId} {stat.level}  {stat.timeRBS} {stat.timeNRBS} {stat.timeLS}</tr>
              </div>
      });
		return (
      <div>
        <h1>Game Stats</h1>
        <table id="customerTable" >
        						<thead>
        							<tr>
        								<th>Game Id</th>
                        <th>Game Id</th>
                        <th>Game Id</th>
                        <th>Game Id</th>
                        <th>Game Id</th>
        							</tr>
        						</thead>
        						<tbody>
        {listItems}
        </tbody>
        </table>
      </div>
		)
	}
}
export default GameStats;
