import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


function Square(props) {
    return (
      <button
        className="square"
        onClick={props.onClick}
      >
        {props.value}
      </button>
    );
  }

  function Square2(props) {
      return (

          <input size="2" maxlength="1" readOnly={props.readOnlyVal}
            type="text"
            placeholder=""
            name={props.value}
            value={props.value}
            //onChange={props.onChange}
            //onChange={props.onChange}
            onChange={(event) => props.onChangeHandler}
          />

      );
    }

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        squares: Array(6).fill(''),
        solution: Array(6).fill(''),
        puzzle: Array(6).fill(''),
        //board: Array(6).fill(null),
        board: Array(2).fill({value:'',given:false}),
        xIsNext: true,
        readOnlyVal:false
        };
        this.valChangeHandler = this.valChangeHandler.bind(this);
    }

  handleClick(i) {
    const squares = this.state.squares.slice();
    if (calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? 'X' : 'O';
    this.setState({
      squares: squares,
      xIsNext: !this.state.xIsNext,
    });
  }

  displayCell(i) {
    return (
      <Square
        value={this.state.squares[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }

  componentDidMount(){
    // var solution = this.state.solution;
    // var puzzle = this.state.puzzle;
    // var board = puzzle;
    var solution = [[{value:1,pencilMark:[],visited:[]},{value:2,pencilMark:[],visited:[]},
    {value:9,pencilMark:[],visited:[]}],[{value:4,pencilMark:[],visited:[]},{value:5,pencilMark:[],visited:[]},
    {value:6,pencilMark:[],visited:[]}]];
    var puzzle = [[{value:1,given:true},{value:null,given:false},{value:null,given:false}],
    [{value:null,given:false},{value:5,given:true},{value:null,given:false}]];

    var board = puzzle;



    this.setState({solution: solution, puzzle: puzzle, board: board});
  }
  onPlayAgainHandler(){
    // var solution = this.state.solution;
    // var puzzle = this.state.puzzle;
    // var board = puzzle;
    // var solution = [[{value:1,pencilMark:[],visited:[]},{value:2,pencilMark:[],visited:[]},
    // {value:9,pencilMark:[],visited:[]}],[{value:4,pencilMark:[],visited:[]},{value:5,pencilMark:[],visited:[]},
    // {value:6,pencilMark:[],visited:[]}]];
    // var puzzle = [[{value:1,given:true},{value:null,given:false},{value:null,given:false}],
    // [{value:null,given:false},{value:5,given:true},{value:null,given:false}]];

    var board = this.state.puzzle;
console.log(board);

    this.setState({board: board});
  }

  valChangeHandler = (i,j,event) => {
    const board = this.state.board.slice();


    board[i][j].value = Number(event.target.value);
console.log(board[i][j].value,i,j);
    this.setState({board: board});
    this.setState({board: board}, function () {
      console.log(this.state.board);
    });
  }

  displayPlayAgainCell() {
    return (
      <button
      style={{backgroundColor:"blue", color:"white", height:"40px", width:"100px"}}
        className="square"
        onClick={() => this.onPlayAgainHandler()}
      >
        Play Again
      </button>
    );
  }

  displayStopCell() {
    return (
      <button
      style={{backgroundColor:"blue", color:"white", height:"40px", width:"100px"}}
        className="square"
        onClick={() => this.onStopHandler()}
      >
        Stop
      </button>
    );
  }

  displaySeeSolutionsCell() {
    return (
      <button
      style={{backgroundColor:"blue", color:"white", height:"40px", width:"100px"}}
        className="square"
        onClick={() => this.onSeeSolutionsHandler()}
      >
        Show Solution
      </button>
    );
  }

  displayProgressCell() {
    return (
      <button
      style={{backgroundColor:"blue", color:"white", height:"40px", width:"100px"}}
        className="square"
        onClick={() => this.onStopHandler()}
      >
        How am I doing?
      </button>
    );
  }
  displayCellForm(i,j) {
    return (
      // <Square2
      //   value={this.state.board[i][j]?this.state.board[i][j].value:""}
      //
      //   //readOnlyVal={this.state.readOnlyVal}
      //   // readOnlyVal={this.state.board[i].given}
      //
      //   onChangeHandler={(event) => this.valChangeHandler(i,j)}
      //   //onChange={this.valChangeHandler(i,j)}
      // />
      <input size="2" maxlength="1" readOnly={this.state.readOnlyVal}
        type="text"
        placeholder=""
        //name={this.state.board[i][j]?this.state.board[i][j].value:""}
        value={this.state.board[i][j]?this.state.board[i][j].value:""}
        onChange={(event) => this.valChangeHandler(i,j,event)}
        //readOnlyVal={this.state.board[i].given}
      />

    );
  }
  render() {
    const winner = calculateWinner(this.state.squares);
    let status;
    if (winner) {
      status = 'Winner: ' + winner;
    } else {
      status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
    }

    return (
      <div>
        <div className="status">{status}</div>

        <form  >
        <div className="board-row">
          {this.displayCellForm(0,0)}
          {this.displayCellForm(0,1)}
          {this.displayCellForm(0,2)}
        </div>
        <div className="board-row">
          {this.displayCellForm(1,0)}
          {this.displayCellForm(1,1)}
          {this.displayCellForm(1,2)}
        </div>
        </form>
        <br></br>
        <div className="board-row">
          {this.displayPlayAgainCell()}
        </div>
        <br></br>
        <div className="board-row">
          {this.displaySeeSolutionsCell()}
        </div>
        <br></br>
        <div className="board-row">
          {this.displayProgressCell()}
        </div>
      </div>
    );
  }
}

class TTT extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

// ========================================

// render(
//   <TTT />,
//   document.getElementById('app')
// );


function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}


export default TTT;
