import React, { Component } from 'react';
import {
	withRouter
} from 'react-router-dom';

class Create extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        name: 'o',
        qty: 'o',
        price: 0
      };
      this.nameChangeHandler = this.nameChangeHandler.bind(this);
      this.qtyChangeHandler = this.qtyChangeHandler.bind(this);
      this.priceChangeHandler = this.priceChangeHandler.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
      this.canBeSubmitted = this.canBeSubmitted.bind(this);
      this.validateName = this.validateName.bind(this);
      this.validateQty = this.validateQty.bind(this);
      this.validatePrice = this.validatePrice.bind(this);
     // this.errorMsgFor = this.errorMsgFor.bind(this);
    }

    nameChangeHandler = (event) => {
      this.setState({name: event.target.value});
    }

    qtyChangeHandler = (event) => {
      this.setState({qty: event.target.value}, function () {
        this.validateQty();
      });
    }

    priceChangeHandler = (event) => {
      this.setState({price: event.target.value}, function () {
        this.validatePrice();
      });
    }

    handleSubmit = (event) => {
      event.preventDefault();
      if (!this.canBeSubmitted()) {
        return;
      }

     if(this.props.match.params.id == undefined){
        this.props.addProduct({name:this.state.name, price:this.state.price,qty:this.state.qty});
     } else {
        this.props.editProduct(this.props.match.params.id,{id:Number(this.props.match.params.id),name:this.state.name, price:this.state.price,qty:this.state.qty});
     }

      this.setState({name:'', price:'',qty:''});
      this.props.history.push("/products/");
    }

    canBeSubmitted() {
      //const chk =
      const price = this.state.price;
      const ckhPrice = (price.length == 0);
      const qty = this.state.qty;
      const ckhQty = (qty.length == 0);
      return !ckhPrice && !ckhQty && !this.validateName() && !this.validateQty() && !this.validatePrice();
    }

    handleReset() {
      this.setState({name:'', price:'',qty:''});
    }

    validateFieldExistsName(inputField) {
            const name = this.state.name;
            return name.length == 0;
    }

    validateFieldExistsQty(inputField) {
      const qty = this.state.qty;
      return qty.length == 0;
}

validateFieldExistsPrice(inputField) {
  const price = this.state.price;
  return price.length == 0;
}
    validateFieldExists(inputField) {
      switch(inputField) {
        case 'name':
            const name = this.state.name;
            return name.length == 0;
        case 'qty':
            const qty = this.state.qty;
            return qty.length == 0;
        case 'price':
            const price = this.state.price;
            return price.length == 0;
        default:
            return ''
      }
    }

    validateName() {
      const name = this.state.name;
      return name.length < 4;
    }

    validatePrice() {
      const price = this.state.price;
      return price < 0;
    }

    validateQty() {
      const qty = this.state.qty;
      return qty.length < 0;
    }

    // errorMsgFor(inputField){
    //   switch(inputField) {
    //     case 'name':
    //         return 'description should be atleast 4 characters';
    //     case 'price':
    //         return 'price should be greater than 0';
    //     case 'url':
    //         return 'url error';
    //     default:
    //         return ''
    //   }}

      errorMsgForName(inputField){
        return 'Products must contain a name of atleast 4 characters';
      }

      errorMsgForQty(inputField){
        return 'Products must contain a Qty.';
      }

      errorMsgForPrice(inputField){
        return 'Products must contain a Price.';
      }

    render() {
      return (
        <div>
          <h1>{this.props.disp}</h1>
          <h3 style={{display: (this.state.name == 0) ? 'block' : 'none', color:"purple" }}>
          Product must contain a Name.</h3>
          <h3 style={{display: (this.state.qty == 0) ? 'block' : 'none', color:"purple" }}>
          Product must contain a Qty.</h3>
          <h3 style={{display: (this.state.price == 0) ? 'block' : 'none', color:"purple" }}>
          Product must contain a Price.</h3>

          <div style={{display: (this.validateName()&& !this.validateFieldExists('name')) ? 'block' : 'none', color:"red" }}>
            <div className="validation-error">
              <span className="text">{this.errorMsgForName("name")}</span>
            </div>
          </div>

          <form onSubmit={this.handleSubmit} >
            <div>
            Name
              <input
                type="text"
                placeholder=""
                value={this.state.name}
                onChange={this.nameChangeHandler}
              />

            </div>
            <div>
              Price
              <input
                type="text"
                placeholder=""
                value={this.state.price}
                onChange={this.priceChangeHandler}
              />

            </div>
            <div>
              Qty
              <input
                type="text"
                placeholder=""
                value={this.state.qty}
                onChange={this.qtyChangeHandler}
              />

            </div>
            <button disabled={!this.canBeSubmitted()}>{this.props.buttonTxt}</button>
          </form>
          <button style={{backgroundColor:"purple"}}
  onClick={(e) => this.handleReset(e)}type="button" >Reset</button>
        </div>

      )
    }
  }

  export default withRouter(Create);
