import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


function Square(props) {
    return (
      // <button
      //   style={{backgroundColor:"blue", color:"white", height:"40px", width:"100px"}}
      //   className="square"
      //   onClick={() => this.onPlayAgainHandler()}
      // >
      //   Play Again
      // </button>
      <button
        style={props.style}
        className="square"
        onClick={props.onClick}
      >
        {props.value}
      </button>
    );
  }

  function Square2(props) {
      return (

          // <input size="2" maxlength="1" readOnly={props.readOnlyVal}
          //   type="text"
          //   placeholder=""
          //   name={props.value}
          //   value={props.value}
          //   //onChange={props.onChange}
          //   //onChange={props.onChange}
          //   onChange={(event) => props.onChangeHandler}
          // />

          <input size="2" maxLength="1" readOnly={props.readOnlyVal}
            type="text"
            placeholder=""
            //readOnly={this.state.puzzle[i][j]?this.state.puzzle[i][j].given:false}
            //style={{fontWeight: 'bold'}}
            style={props.style}
            key={props.key}
            defaultValue={props.defaultValue}
            onChange={props.onChange}
            onClick={props.onClick}
          />

      );
    }

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        squares: Array(6).fill(''),
        solution: Array(6).fill(''),
        puzzle: Array(6).fill(''),
        //board: Array(6).fill(null),
        board: Array(2).fill({value:'',given:false}),
        cellCheck: Array(2).fill(false).map(() => new Array(3).fill(false)),
        saved_board:Array(2).fill({value:'',given:false}),
        xIsNext: true,
        readOnlyVal:false,
        buttonText:"See Solution"
        };
        this.valChangeHandler = this.valChangeHandler.bind(this);

    }

  handleClick(i) {
    const squares = this.state.squares.slice();
    if (calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? 'X' : 'O';
    this.setState({
      squares: squares,
      xIsNext: !this.state.xIsNext,
    });
  }

  displayCell(i) {
    return (
      <Square
        value={this.state.squares[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }

  componentDidMount(){
    // var solution = this.state.solution;
    // var puzzle = this.state.puzzle;
    // var board = puzzle;
    var solution = [[{value:1,pencilMark:[],visited:[]},{value:2,pencilMark:[],visited:[]},
    {value:9,pencilMark:[],visited:[]}],[{value:4,pencilMark:[],visited:[]},{value:5,pencilMark:[],visited:[]},
    {value:6,pencilMark:[],visited:[]}]];
    var puzzle = [[{value:1,given:true},{value:undefined,given:false},{value:undefined,given:false}],
    [{value:undefined,given:false},{value:5,given:true},{value:undefined,given:false}]];

    //const board = puzzle.slice();
    const board = this.deepCopy(puzzle);

if(board ===  puzzle){console.log('equal')}else{console.log('not equal')}



  //  this.setState({solution: solution, puzzle: puzzle, board: board});
    this.setState({solution: solution, puzzle: puzzle, board: board}, function () {
      console.log('in mount bo',this.state.board);
      console.log('in mount pu',this.state.puzzle);
      console.log('in mount sol',this.state.solution);
    });
  }

  onPlayAgainHandler(){
    // var board = [[{value:1,given:true},{value:null,given:false},{value:undefined,given:false}],
    // [{value:undefined,given:false},{value:5,given:true},{value:undefined,given:false}]];
    var board = this.deepCopy(this.state.puzzle);
    var saved_board = this.deepCopy(this.state.puzzle);
    var buttonText  = "See Solution";
    var cellCheck =  Array(2).fill(false).map(() => new Array(3).fill(false));
    console.log('in onPlayAgainHandler before b',board);

    this.setState({board: board, saved_board: saved_board, buttonText: buttonText, cellCheck: cellCheck}, function () {
      console.log('in onPlayAgainHandler fter b',this.state.board);
    });
  }
  onSeeSolutionsHandler(){

    console.log('in onSeeSolutionsHandler before');
    var buttonText = this.state.buttonText;
    var cellCheck =  Array(2).fill(false).map(() => new Array(3).fill(false));
    var saved_board;
    var board;

    if(buttonText == "See Solution"){
      buttonText  = "Back To Puzzle";
      saved_board = this.deepCopy(this.state.board);
      board       = this.deepCopy(this.state.solution);
    } else {
      buttonText  = "See Solution";
      board       = this.deepCopy(this.state.saved_board);
    }


    console.log('in onSeeSolutionsHandler before b',board);

    this.setState({board: board, saved_board: saved_board, buttonText: buttonText, cellCheck: cellCheck}, function () {
      console.log('in onSeeSolutionsHandler fter b',this.state.board);
      console.log('in onSeeSolutionsHandler fter sb',this.state.saved_board);
    });
  }

  onCellCheckHandler(){

