import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

function Square(props) {
    return (
      <div>
      <button
        className="square"
        onClick={props.onClick}
      >
        {props.value}
      </button>
      <button
        className="square"
        onClick={props.onClick}
      >
        {props.value}
      </button>
      </div>
    );
  }

  function Square2(props) {
      return (
        <div>
          Price
          <input
            type="text"
            placeholder=""
            value={props.value}

          />
          </div>
      );
    }

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        squares: Array(9).fill('I'),
        xIsNext: true,
        };
    }

  handleClick(i) {
    const squares = this.state.squares.slice();
    if (calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? 'X' : 'O';
    this.setState({
      squares: squares,
      xIsNext: !this.state.xIsNext,
    });
  }

  displayCell(i) {
    return (
      <Square
        value={this.state.squares[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }

  displayCellForm(i) {
    return (
      <Square2
        value={this.state.squares[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }
  render() {
    const winner = calculateWinner(this.state.squares);
    let status;
    if (winner) {
      status = 'Winner: ' + winner;
    } else {
      status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
    }

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.displayCell(0)}
          {this.displayCell(1)}
          {this.displayCell(2)}
        </div>
        <div className="board-row">
          {this.displayCell(3)}
          {this.displayCell(4)}
          {this.displayCell(5)}
        </div>
        <div className="board-row">
          {this.displayCell(6)}
          {this.displayCell(7)}
          {this.displayCell(8)}
        </div>


        <form  >
        {this.displayCellForm(7)}
        {this.displayCellForm(8)}
        </form>
      </div>
    );
  }
}

class TTT extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

